function init() {
    // Ajoutez votre logique JavaScript ici
    var counters = [0, 0, 0, 4, 3, 5, 7]; // Exemple de compteurs initiaux pour chaque appareil

    // Mettez Ã  jour les textes avec les valeurs des compteurs
    for (var i = 0; i < counters.length; i++) {
        var textElement = document.getElementById(i.toString());
        if (textElement) {
            textElement.firstChild.textContent = counters[i];
        }
    }
}
